from web3 import Web3
from hexbytes import HexBytes

NEW_EOA = '0x49A9deCA3DcA86aB3A029C2ed629EC8477009Fee'
OLD_EOA = '0x4e5b2E1Dc63f6B91cb6cD759936495434c7E0000'
NEW_CONTRACT = '0x2320A28f52334d62622cc2EaFa15DE55F9987eD0'


class Web3Mock:
    def __init__(self):
        self.eth = EthMock()


class EthMock:
    def __init__(self):
        self.contract = ContractMock()

    def get_transaction_count(self, address):
        if address == Web3.toChecksumAddress(NEW_EOA) or address == Web3.toChecksumAddress(NEW_CONTRACT):
            return 0
        if address == Web3.toChecksumAddress(OLD_EOA):
            return 1
        return 0
        
    chain_id = 1
    
    def get_code(self, address):
        if address == Web3.toChecksumAddress(NEW_CONTRACT):
            return HexBytes(
                ''
            )
        elif address == Web3.toChecksumAddress(NEW_EOA) or address == Web3.toChecksumAddress(OLD_EOA):
            return HexBytes('0x')
     
        return HexBytes('0x')
     

