import time
import agent
import pytest
from unittest.mock import patch
from hexbytes import HexBytes
from forta_bot import FindingSeverity, create_transaction_event
from constants import SQUID_ROUTER_ADDRESS, SQUID_RELAYER, WRAPPED_NATIVE_TOKEN_ADDRESSES
from web3_mock import Web3Mock, NEW_EOA, OLD_EOA, NEW_CONTRACT
from web3 import AsyncWeb3

w3 = Web3Mock()
w3.to_checksum_address = AsyncWeb3.to_checksum_address
real_w3 = AsyncWeb3.AsyncHTTPProvider

class TestSquidFundingAgent:

    @pytest.mark.asyncio
    async def test_transfer_to_contract(self):
        await agent.initialize()
        # Contract passed in transaction.data
        tx_event = create_transaction_event(
            transaction= {
                'hash': "0",
                'to': SQUID_ROUTER_ADDRESS,
                'from': SQUID_RELAYER,
                'value': "0",
                'data': ''
            },
            block={
                    'number': 0
            },
            chain_id=1,
            logs = [
                {
                'address': WRAPPED_NATIVE_TOKEN_ADDRESSES[1],
                'data': '0x0000000000000000000000000000000000000000000000000928296cc9a1b390',
                'topics':[HexBytes("0x7fcf532c15f0a6db0bd6d0e038bea71d30d808c7d98cb3bf7268a95bf5081b65"),
                          HexBytes("0x0000000000000000000000004fd39c9e151e50580779bd04b1f7ecc310079fd3")]
                }
            ],
            )

        findings = await agent.detect_squid_funding(w3, tx_event)
        assert len(findings) == 0, "This should have not triggered a finding as the to is a contract"


    @pytest.mark.asyncio
    async def test_not_transfer_from_squid(self):
        await agent.initialize()

        tx_event = create_transaction_event(
            transaction= {
                'hash': "0",
                'to': NEW_CONTRACT,
                'from': "0x56f879e6586bfacafd5219207a33cae1dace7c58",
                'value': "0",
                'data': ''
            },
            block={
                    'number': 0
            },
            chain_id=1,
            logs = [{
                'address': WRAPPED_NATIVE_TOKEN_ADDRESSES[1],
                'data': '0x0000000000000000000000000000000000000000000000000928296cc9a1b390',
                'topics':[HexBytes("0x7fcf532c15f0a6db0bd6d0e038bea71d30d808c7d98cb3bf7268a95bf5081b65"),
                          HexBytes("0x0000000000000000000000004fd39c9e151e50580779bd04b1f7ecc310079fd3")]
            }],
            )

        findings = await agent.detect_squid_funding(w3, tx_event)
        assert len(findings) == 0, "This should have not triggered a finding as the to is not Squid"


    @pytest.mark.asyncio
    @patch('agent.get_chain_id', return_value=1)
    async def test_transfer_from_squid_to_new_account(self, mocker):
        await agent.initialize()
        # New EOA passed in transaction.data
        tx_event =  create_transaction_event(
            transaction= {
                'hash': "0",
                'to': SQUID_ROUTER_ADDRESS,
                'from': SQUID_RELAYER,
                'value': "0",
                'data': ''
            },
            block={
                    'number': 0
            },
            chain_id=1,
            logs = [{
                'address': WRAPPED_NATIVE_TOKEN_ADDRESSES[1],
                'data': '0x00000000000000000000000000000000000000000000000038d0373a011e0b13',
                'topics':[HexBytes("0x7fcf532c15f0a6db0bd6d0e038bea71d30d808c7d98cb3bf7268a95bf5081b65"),
                          HexBytes("0x0000000000000000000000004fd39c9e151e50580779bd04b1f7ecc310079fd3")]
            }]
            )

        findings = await agent.detect_squid_funding(w3, tx_event)
        assert len(findings) == 1, "This should have triggered a finding"
        assert findings[0].alert_id == "FUNDING-SQUID-NEW-ACCOUNT", "This is a tx from Squid to a new account"
        assert findings[0].severity == FindingSeverity.Medium, "Severity should be medium"


