from web3 import Web3
from hexbytes import HexBytes

NEW_EOA = '0x52d56c1ade01525dbe24c89bba0a726cf3a30c76'
OLD_EOA = '0x02d56c1ade01525dbe24c89bba0a726cf3a30c76'
NEW_CONTRACT = '0x8abe851bcc4fd1986c3d1ef8978fad86a26a0c57'

MOCK_TX_INPUT_TO_DECODED_INPUT_MAPPING = {
    "": ('<Function transfer(tuple[])>', {'_transfers': [((0, '0x0000000000000000000000000000000000000000', 0), '0x52D56c1AdE01525DBE24C89bBA0a726cf3A30c76', 0)]}),
    "": ('<Function transfer(tuple[])>', {'_transfers': [((0, '0x0000000000000000000000000000000000000000', 0), '0x02D56c1AdE01525DBE24C89bBA0a726cf3A30c76', 0)]}),
    "": ('<Function transfer(tuple[])>', {'_transfers': [((0, '0x0000000000000000000000000000000000000000', 0), '0x12D56c1AdE01525DBE24C89bBA0a726cf3A30c76', 0)]}),
}


class Web3Mock:
    def __init__(self):
        self.eth = EthMock()


class EthMock:
    def __init__(self):
        self.contract = ContractMock()

    async def get_transaction_count(self, address, block_number):
        if address == Web3.toChecksumAddress(NEW_EOA) or address == Web3.toChecksumAddress(NEW_CONTRACT):
            return 0
        if address == Web3.toChecksumAddress(OLD_EOA):
            return 100
        return 0

    chain_id = 1

    async def get_code(self, address):
        if address == Web3.toChecksumAddress(NEW_CONTRACT):
            return HexBytes(
                ''
            )
        elif address == Web3.toChecksumAddress(NEW_EOA) or address == Web3.toChecksumAddress(OLD_EOA):
            return HexBytes('0x')

        return HexBytes('0x')


class ContractMock:
    def __init__(self):
        self.functions = FunctionsMock()

    def __call__(self, *args, **kwargs):
        return self

    def decode_function_input(self, transaction_input):
        return MOCK_TX_INPUT_TO_DECODED_INPUT_MAPPING[transaction_input]


class FunctionsMock:
    def __init__(self):
        self.return_value = None

    async def call(self, *_, **__):
        return self.return_value
